import React from 'react';
import { useState } from 'react';
import Header from './components/Header';
import GrainsContainer from './components/GrainsContainer';
import Footer from './components/Footer';
import TimerContainer from './components/TimerContainer';

function App() {
  const [showTimer, setShowTimer] = useState(false);
  const [timerName, setTimerName] = useState('');

  const toggleTimer = function (e) {
    if (e.target.className === 'grains-img') {
      setShowTimer(!showTimer);
      setTimerName(e.target.alt);
    }
  };

  return (
    <div className="main">
      <Header />
      {showTimer === false ? (
        <GrainsContainer
          style={{ filter: 'brightness(50%)' }}
          onClick={toggleTimer}
        />
      ) : (
        <>
          <GrainsContainer onClick={toggleTimer} />
          <TimerContainer showTimer={showTimer} name={timerName} />
        </>
      )}
      {/* <Footer /> */}
    </div>
  );
}

export default App;
