import React from 'react';
import ButtonClose from './ButtonClose';
import ButtonStart from './ButtonStart';
import { useState, useRef } from 'react';

function TimerContainer({ onClick, name, grainsTime, showTimer }) {
  const [timeValue, setTimeValue] = useState(
    `${grainsTime.toString().padStart(2, '0')}:00`
  );
  const [btnText, setBtnText] = useState('START');
  const [isPlaying, setIsPlaying] = useState(false);
  const myRef = useRef();

  const playPause = function () {
    isPlaying ? myRef.current.pause() : myRef.current.play();
    setIsPlaying(true);
  };

  let timeLeft = {
    total: grainsTime * 60,
    min: grainsTime,
    sec: 0,
  };

  const updateTimer = function () {
    setTimeValue(
      `${timeLeft.min.toString().padStart(2, '0')}:${timeLeft.sec
        .toString()
        .padStart(2, '0')}`
    );
  };

  const getTimeLeft = function (end) {
    const curTime = Date.parse(new Date());
    const difference = end - curTime;
    let { total } = timeLeft;
    total = difference / 1000;

    const min = Math.trunc(((timeLeft.total - 1) / 60) % 60);
    const sec = (timeLeft.total - 1) % 60;

    return {
      total,
      min,
      sec,
    };
  };

  const handleStartTimer = function (e) {
    const end = Date.parse(new Date()) + timeLeft.total * 1000;
    setBtnText(`Don't forget to stir!`);
    if (btnText === `Don't forget to stir!` || btnText === `Stop the sound`)
      return;

    const interval = setInterval(() => {
      timeLeft = getTimeLeft(end);
      updateTimer();

      if (timeLeft.total <= 0) {
        playPause();
        clearInterval(interval);
        setBtnText(`Stop the sound`);
      }
    }, 1000);
  };

  return (
    <div className="timer-overlay">
      <div className="timer-container">
        <h2>{name}</h2>
        <ButtonClose onClick={onClick} />
        <div className="timer-container__timer">
          <svg
            className="timer-container__timer--circle"
            viewBox="0 0 100 100"
            xmlns="http://www.w3.org/2000/svg"
          >
            <circle cx="50" cy="50" r="40" />
          </svg>
          <p>{timeValue}</p>
        </div>
        <ButtonStart
          onClick={btnText === `Stop the sound` ? onClick : handleStartTimer}
          text={btnText}
        />
        <audio ref={myRef} src="timer.mp3"></audio>
        <img className="timer-container__img" src="img/wheat-bg.svg" alt="" />
      </div>
    </div>
  );
}

export default TimerContainer;
