import React from 'react';
import { useState } from 'react';
import Header from './components/Header';
import GrainsContainer from './components/GrainsContainer';
import Footer from './components/Footer';
import TimerContainer from './components/TimerContainer';

function App() {
  const [showTimer, setShowTimer] = useState(false);

  const openTimer = function (e) {
    if (e.target.className === 'grains-img') console.log(e.target);
  };

  return (
    <>
      <Header />
      <GrainsContainer onClick={openTimer} />
      <TimerContainer showTimer={showTimer} />
      <Footer />
    </>
  );
}

export default App;
