import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import GrainsContainer from './components/GrainsContainer';
import TimerContainer from './components/TimerContainer';
import Loader from './components/Loader';

function App() {
  const [showTimer, setShowTimer] = useState(false);
  const [timerName, setTimerName] = useState('');
  const [grainsTime, setGrainsTime] = useState(15);
  const [loading, setLoading] = useState(true);

  const handleToggleTimer = function (e) {
    if (
      e.target.className === 'grains-img' ||
      e.target.className === 'timer-container--btn-close' ||
      e.target.textContent === 'STOP'
    ) {
      setShowTimer(!showTimer);
      setTimerName(e.target.alt);

      switch (e.target.alt) {
        case 'MILLET':
        case 'PEARL BARLEY':
        case 'BARLEY':
          setGrainsTime(45);
          break;
        case 'COUSCOUS':
          setGrainsTime(5);
          break;
        case 'FARRO':
          setGrainsTime(60);
          break;
        default:
          setGrainsTime(15);
      }
    }
  };

  // useEffect(() => {
  //   document
  //     .querySelector('.grains-container')
  //     .addEventListener('load', () => setLoading(false));
  // }, []);
  window.addEventListener('load', () => setLoading(false));
  return (
    <main className="main">
      <Header />
      {loading ? (
        <Loader />
      ) : !showTimer ? (
        <GrainsContainer onClick={handleToggleTimer} />
      ) : (
        <>
          <GrainsContainer onClick={handleToggleTimer} />
          <TimerContainer
            onClick={handleToggleTimer}
            name={timerName}
            grainsTime={grainsTime}
          />
        </>
      )}
    </main>
  );
}

export default App;
