import React from 'react';
import { useState } from 'react';
import Header from './components/Header';
import GrainsContainer from './components/GrainsContainer';
import Footer from './components/Footer';
import TimerContainer from './components/TimerContainer';

function App() {
  const [showTimer, setShowTimer] = useState(false);

  const toggleTimer = function (e) {
    if (e.target.className === 'grains-img') setShowTimer(!showTimer);
    console.log(e.target.alt);
  };

  return (
    <>
      <Header />
      {showTimer === false ? (
        <GrainsContainer onClick={toggleTimer} />
      ) : (
        <TimerContainer showTimer={showTimer} />
      )}
      <Footer />
    </>
  );
}

export default App;
