import React from 'react';
import { useState } from 'react';
import Header from './components/Header';
import GrainsContainer from './components/GrainsContainer';
import Footer from './components/Footer';
import TimerContainer from './components/TimerContainer';

function App() {
  const [showTimer, setShowTimer] = useState(false);
  const [timerName, setTimerName] = useState('');
  const [grainsTime, setGrainsTime] = useState(15);

  const handleToggleTimer = function (e) {
    if (
      e.target.className === 'grains-img' ||
      e.target.className === 'timer-container--btn-close' ||
      e.target.textContent === 'Stop the sound'
    ) {
      setShowTimer(!showTimer);
      setTimerName(e.target.alt);
      setGrainsTime(15);

      if (
        e.target.alt === 'MILLET' ||
        e.target.alt === 'PEARL BARLEY' ||
        e.target.alt === 'BARLEY'
      ) {
        setGrainsTime(45);
      } else if (e.target.alt === 'COUSCOUS') {
        setGrainsTime(0.1);
      } else if (e.target.alt === 'FARRO') {
        setGrainsTime(60);
      }
    }
  };

  return (
    <div className="main">
      <Header />
      {showTimer === false ? (
        <GrainsContainer onClick={handleToggleTimer} />
      ) : (
        <>
          <GrainsContainer onClick={handleToggleTimer} />
          <TimerContainer
            showTimer={showTimer}
            onClick={handleToggleTimer}
            name={timerName}
            grainsTime={grainsTime}
          />
        </>
      )}
      <Footer />
    </div>
  );
}

export default App;
