import React from 'react';
import ButtonClose from './ButtonClose';
import ButtonStart from './ButtonStart';
import TimerValue from './TimerValue';
import { useState } from 'react';

function TimerContainer({ onClick, name, grainsTime }) {
  const [timeValue, setTimeValue] = useState('');

  const timeLeft = {
    total: { grainsTime } * 60,
    min: { grainsTime },
    sec: 0,
  };

  const updateTimer = function () {
    setTimeValue(
      `${timeLeft.min.toString().padStart(2, '0')}:${timeLeft.sec
        .toString()
        .padStart(2, '0')}`
    );
  };

  const getTimeLeft = function () {};

  const handleStartTimer = function (e) {
    const end = Date.parse(new Date()) + timeLeft.total * 1000;

    e.target.dataset = 'started';
    e.target.textContent = `Don't forget to stir!`;

    const interval = setInterval(() => {
      timeLeft = getTimeLeft(end);
      updateTimer();

      if (timeLeft.total <= 0) clearInterval(interval);
    }, 1000);
  };

  return (
    <div className="timer-container">
      <h2>{name}</h2>
      <ButtonClose onClick={onClick} />
      <div className="timer-container__timer">
        <svg
          className="timer-container__timer--circle"
          viewBox="0 0 100 100"
          xmlns="http://www.w3.org/2000/svg"
        >
          <circle cx="50" cy="50" r="40" />
        </svg>
        <TimerValue />
      </div>
      <ButtonStart onClick={handleStartTimer} />
    </div>
  );
}

export default TimerContainer;
