import React from 'react';
import ButtonClose from './ButtonClose';
import ButtonStart from './ButtonStart';
import { useState } from 'react';

function TimerContainer({ onClick, name, grainsTime }) {
  const [timeValue, setTimeValue] = useState(`${grainsTime}:00`);
  const [btnText, setBtnText] = useState('START');

  const audio = new Audio('/timer.mp3');

  let timeLeft = {
    total: grainsTime * 60,
    min: grainsTime,
    sec: 0,
  };

  const updateTimer = function () {
    setTimeValue(
      `${timeLeft.min.toString().padStart(2, '0')}:${timeLeft.sec
        .toString()
        .padStart(2, '0')}`
    );
  };

  const getTimeLeft = function (end) {
    const curTime = Date.parse(new Date());
    const difference = end - curTime;
    let { total } = timeLeft;
    total = difference / 1000;

    const min = Math.trunc((timeLeft.total / 60) % 60);
    const sec = timeLeft.total % 60;

    return {
      total,
      min,
      sec,
    };
  };

  const handleStartTimer = function (e) {
    const end = Date.parse(new Date()) + timeLeft.total * 1000;
    setBtnText(`Don't forget to stir!`);
    if (btnText === `Don't forget to stir!`) return;
    console.log('!' + { timeLeft });

    const interval = setInterval(() => {
      timeLeft = getTimeLeft(end);
      updateTimer();

      console.log(timeLeft);

      if (timeLeft.total < 0) {
        audio.play();
        clearInterval(interval);
        setBtnText(`Stop the sound`);
      }
    }, 1000);
  };

  const handleStopSound = function () {
    audio.pause();
    console.log('audio pause');
  };

  return (
    <div className="timer-container">
      <h2>{name}</h2>
      <ButtonClose onClick={onClick} />
      <div className="timer-container__timer">
        <svg
          className="timer-container__timer--circle"
          viewBox="0 0 100 100"
          xmlns="http://www.w3.org/2000/svg"
        >
          <circle cx="50" cy="50" r="40" />
        </svg>
        <p>{timeValue}</p>
      </div>
      <ButtonStart onClick={handleStartTimer} text={btnText} />
    </div>
  );
}

export default TimerContainer;
